/**
 * Generates APM Tool release notes HTML markup from template and data array
 * @param {Array} releaseNotesData - Array of objects with properties: dynatraceComponent, version, summary
 * @returns {string} - Complete HTML markup with populated release notes
 */
function generateReleaseNotesHTML(releaseNotesData) {
    // Validate input
    if (!Array.isArray(releaseNotesData)) {
        throw new Error('Input must be an array of release note objects');
    }

    // Get current date in DD.MM.YYYY format
    const today = new Date();
    const formattedDate = `${today.getDate().toString().padStart(2, '0')}.${(today.getMonth() + 1).toString().padStart(2, '0')}.${today.getFullYear()}`;

    // HTML template with placeholders
    const htmlTemplate = `<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>APM Tool Release Notes - ${formattedDate}</title>
    <style>
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            line-height: 1.6;
            color: #333;
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            min-height: 100vh;
            padding: 20px;
        }

        .container {
            max-width: 800px;
            margin: 0 auto;
            background: white;
            border-radius: 12px;
            box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
            overflow: hidden;
        }

        .header {
            background: linear-gradient(135deg, #1e3c72 0%, #2a5298 100%);
            color: white;
            padding: 40px 30px;
            text-align: center;
        }

        .header h1 {
            font-size: 2.5em;
            margin-bottom: 10px;
            font-weight: 300;
        }

        .header p {
            font-size: 1.1em;
            opacity: 0.9;
        }

        .content {
            padding: 40px 30px;
        }

        .release-notes {
            list-style: none;
        }

        .release-note {
            background: #f8f9fa;
            border-left: 4px solid #2a5298;
            padding: 25px;
            margin-bottom: 20px;
            border-radius: 0 8px 8px 0;
            transition: transform 0.2s ease, box-shadow 0.2s ease;
        }

        .release-note:hover {
            transform: translateY(-2px);
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
        }

        .component-name {
            font-size: 1.4em;
            font-weight: 600;
            color: #1e3c72;
            margin-bottom: 10px;
            display: flex;
            align-items: center;
        }

        .component-name::before {
            content: "üîß";
            margin-right: 10px;
            font-size: 1.2em;
        }

        .component-description {
            font-size: 1em;
            color: #555;
            line-height: 1.7;
        }

        .footer {
            background: #f1f3f4;
            text-align: center;
            padding: 20px;
            color: #666;
            font-size: 0.9em;
        }

        @media (max-width: 768px) {
            .container {
                margin: 10px;
                border-radius: 8px;
            }

            .header {
                padding: 30px 20px;
            }

            .header h1 {
                font-size: 2em;
            }

            .content {
                padding: 30px 20px;
            }

            .release-note {
                padding: 20px;
            }
        }
    </style>
</head>
<body>
    <div class="container">
        <header class="header">
            <h1>APM Tool release notes for ${formattedDate}</h1>
            <p>List of release notes for APM Tool components</p>
        </header>

        <main class="content">
            <ul class="release-notes">
                {{RELEASE_NOTES_CONTENT}}
            </ul>
        </main>

        <footer class="footer">
            <p>¬© 2025 APM Tool - Application Performance Monitoring Solution</p>
        </footer>
    </div>
</body>
</html>`;

    // Generate release notes content
    const releaseNotesContent = releaseNotesData.map(item => {
        // Validate required properties
        const dynatraceComponent = item.dynatraceComponent || 'Unknown Component';
        const version = item.version || 'Unknown Version';
        const summary = item.summary || 'No summary available';

        // Escape HTML to prevent XSS attacks
        const escapedComponent = escapeHtml(dynatraceComponent);
        const escapedVersion = escapeHtml(version);
        let escapedSummary = escapeHtml(summary);
        escapedSummary = formatTopicNames(escapedSummary);

        return `                <li class="release-note">
                    <div class="component-name">${escapedComponent} ${escapedVersion}</div>
                    <div class="component-description">
                        ${escapedSummary}
                    </div>
                </li>`;
    }).join('\n');

    // Replace placeholder with generated content
    const finalHTML = htmlTemplate.replace('{{RELEASE_NOTES_CONTENT}}', releaseNotesContent);

    return finalHTML;
}

/**
 * Escapes HTML special characters to prevent XSS attacks
 * @param {string} text - Text to escape
 * @returns {string} - Escaped text
 */
function escapeHtml(text) {
    if (typeof text !== 'string') {
        return String(text);
    }

    const map = {
        '&': '&amp;',
        '<': '&lt;',
        '>': '&gt;',
        '"': '&quot;',
        "'": '&#39;'
    };

    return text.replace(/[&<>"']/g, (m) => map[m]);
}

/**
 * Formats topic names in text with proper line breaks and padding
 * @param {string} text - Text containing topic names (e.g., "ANNOUNCEMENTS:", "BREAKING CHANGES:")
 * @returns {string} - Formatted text with proper spacing
 */
function formatTopicNames(text) {
    if (typeof text !== 'string') {
        return String(text);
    }

    // Regular expression to match topic names (capital letters/words followed by colon)
    const topicRegex = /([A-Z][A-Z\s\/]*:)/g;

    let formattedText = text;
    let isFirstTopic = true;

    // Replace each topic name with proper formatting
    formattedText = formattedText.replace(topicRegex, (match, topicName) => {
        if (isFirstTopic) {
            isFirstTopic = false;
            // First topic: just add line break after colon
            return topicName + '<br>';
        } else {
            // Subsequent topics: add 2 empty lines before + line break after colon
            return '<br><br>' + topicName + '<br>';
        }
    });

    return formattedText;
}

return {
  "result": generateReleaseNotesHTML($input.first().json.output)
}
